mapper.insert:
insert into daily_market_amt
(
      post_date
    , stock_code
    , high
    , low
    , open
    , close
    , volume
) values (
     #{postDate}
   , #{stockCode}
   , #{high}
   , #{low}
   , #{open}
   , #{close}
   , #{volume}
)

mapper.update:
update daily_market_amt
   set high        = #{high}
     , low         = #{low}
     , open        = #{open}
     , close       = #{close}
     , volume      = #{volume}
 where post_date   = #{postDate}
   and stock_code  = #{stockCode}
